import React,{useState,useEffect} from 'react';

import {getWeather} from "./Api/GetWeather";


function App() {
    const [weatherInfo,setWeather] = useState();
    const [findCity,setCity] = useState('');

    const cityValue = e => {
        setCity(e.target.value);
    }

  return (
    <>
      <input onChange={cityValue}/>
      <button onClick={async ()=>{
          const cityWeather = await getWeather(findCity);
          await setWeather(cityWeather);

      }}>Search</button>
        <p>{weatherInfo}</p>
    </>
  );
}

export default App;
