import React,{useState,useEffect} from 'react';

import {getWeather} from "./Api/GetWeather";


function App() {
    const [weatherInfo,setWeather] = useState();
    const [findCity,setCity] = useState();

    const cityValue = e => {
        setCity(e.value);
    }
    console.log(findCity);
  return (
    <>
      <input onChange={cityValue}/>
      <button onClick={()=>{
          getWeather(findCity);
      }}>Search</button>
        <p>{weatherInfo}</p>
    </>
  );
}

export default App;
